mapper.update:
UPDATE FAQ
	SET
	faq_no = #{faq_no}
	,sj = #{sj}
	,reg_de = now()
	,con = #{con}
	,hitcnt = #{hitcnt}
	,upd_de = now()
	,del_yn = #{del_yn}
	,reg_mb_no = #{reg_mb_no}
WHERE faq_no = #{faq_no}

mapper.delete:
UPDATE FAQ
	SET
	del_yn = 'y'
	,reg_mb_no = #{reg_mb_no}
	,upd_de = now()
WHERE faq_no = #{faq_no}
