			,REG_DE
			,CON
			,HITCNT
			,UPD_DE
			,DEL_YN
			,REG_MB_NO)
		VALUES(
			CONCAT('FAQ_',#{faq_no}),
			,#{faq_no}
			,#{sj}
			,#{reg_de}
			,#{con}
			,#{hitcnt}
			,#{upd_de}
			,#{del_yn}
			,#{reg_mb_no}
